        var tipo = Type.GetType($"WebAluno.Aluno.{tipoAluno}"); // Namespace totalmente qualificado
        if (tipo == null)
            throw new ArgumentException($"Tipo de aluno '{tipoAluno}' não encontrado.");

        var construtor = tipo.GetConstructor(Type.EmptyTypes); // Ou um construtor com parâmetros
        if(construtor == null)
            throw new ArgumentException($"Construtor para '{tipoAluno}' não encontrado");

        var aluno = (Aluno)construtor.Invoke(null); // Invoca o construtor sem parametros

        // Aqui você pode usar os parametros para configurar o aluno.
        // Exemplo:
        if (parametros.ContainsKey("Nome"))
          aluno.Nome = parametros["Nome"] as string;


        return aluno;
    }
}

