        [StringLength(255, ErrorMessage = "O endereço deve ter no máximo 255 caracteres.")]
        public string? Endereco { get; set; }

        // Construtor para facilitar a criação de instâncias. Considerar Fluent Builder para cenários mais complexos.
        public RequestDtoAluno(int? id, string nome, string endereco)
        {
            Id = id;
            Nome = nome;
            Endereco = endereco;
        }

        public RequestDtoAluno() { } //construtor vazio mantido para compatibilidade com alguns frameworks
    }

