        // Adicionar um novo aluno
        alunos.Add(new Aluno(3, "Ana Pereira", "Rua da Praia, 789"));

        // Salvar as alterações
        manager.SalvarAlunos(alunos);

        Console.WriteLine("Alunos salvos com sucesso!");
    }
}
